
SET NAMES UTF8;
#放弃数据库(如果存在)xianhua
DROP DATABASE IF EXISTS xianhua;
#创建数据库tedu
CREATE DATABASE xianhua CHARSET=UTF8;
#进入数据库
USE xianhua;



#悬停主题大图
CREATE TABLE argument_img(
  lid TINYINT PRIMARY KEY AUTO_INCREMENT,
  dname VARCHAR(64),
  big_img VARCHAR(128),
  small_img VARCHAR(128)
);
