#鲜花详情
CREATE TABLE flower_laptop(
  lid INT PRIMARY KEY AUTO_INCREMENT,
  family_id INT,              #所属型号家族编号
  title VARCHAR(128),         #主标题
  price DECIMAL(10,2),        #价格
  market_price DECIMAL(10,2), #市场价格
  flower_wood  VARCHAR(128),  #花材
  pack VARCHAR(64),           #包装
  flower_language VARCHAR(64),#花语
  fresh VARCHAR(64),          #鲜花附送
  deliver VARCHAR(128),       #配送区域
  note  VARCHAR(128),	      #备注	
  tip   VARCHAR(128)         #小贴士	
);

